import React, { useState, useEffect } from 'react';
import { getProducts, deleteProduct, getProductsByCategory, getCategories } from '../services/api.ts';
import { Product } from '../types';
import { useNavigate, Link } from 'react-router-dom';

const ProductList: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [categories, setCategories] = useState<any[]>([]);
  const [filters, setFilters] = useState({ category: '', minPrice: '', maxPrice: '', inStock: '' });
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [productsRes, categoriesRes] = await Promise.all([getProducts(), getCategories()]);
        setProducts(productsRes.data);
        setCategories(categoriesRes.data);
      } catch (error) {
        console.error("Error cargando datos:", error);
      }
    };
    fetchData();
  }, []);

  const fetchProducts = async (customFilters = {}) => {
    try {
      const response = await getProducts(customFilters);
      setProducts(response.data);
    } catch (error) {
      console.error('Error obteniendo productos:', error);
    }
  };

  const handleDelete = async (id: number) => {
    if (window.confirm('¿Estás seguro de que quieres eliminar este producto?')) {
      try {
        await deleteProduct(id);
        fetchProducts();
      } catch (error) {
        console.error('Error eliminando producto:', error);
      }
    }
  };

  const handleFilterChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  const applyFilters = () => {
    const filterParams: any = {};
    if (filters.category) filterParams.category = filters.category;
    if (filters.minPrice) filterParams.minPrice = filters.minPrice;
    if (filters.maxPrice) filterParams.maxPrice = filters.maxPrice;
    if (filters.inStock) filterParams.inStock = filters.inStock === 'true';
    fetchProducts(filterParams);
  };

  const clearFilters = () => {
    setFilters({ category: '', minPrice: '', maxPrice: '', inStock: '' });
    fetchProducts();
  };

  const handleSpecialQuery = async (queryType: string) => {
    try {
      let response;
      switch (queryType) {
        case 'byCategory':
          if (filters.category) {
            response = await getProductsByCategory(parseInt(filters.category));
            setProducts(response.data);
          }
          break;
        default:
          fetchProducts();
      }
    } catch (error) {
      console.error('Error ejecutando consulta especial:', error);
    }
  };

  return (
    <div className="container">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold text-gray-800">Lista de Productos</h1>
        <Link to="/add-category" className="btn btn-success">+ Agregar Categoría</Link>
      </div>

      <div className="card">
        <h2 className="text-lg font-semibold text-gray-700 mb-4">Filtros</h2>
        <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
          <select name="category" className="input-field" value={filters.category} onChange={handleFilterChange}>
            <option value="">Todas las Categorías</option>
            {categories.map(c => <option key={c.id} value={c.id}>{c.name}</option>)}
          </select>
          <input type="number" name="minPrice" className="input-field" value={filters.minPrice} onChange={handleFilterChange} placeholder="Precio Mín" />
          <input type="number" name="maxPrice" className="input-field" value={filters.maxPrice} onChange={handleFilterChange} placeholder="Precio Máx" />
          <select name="inStock" className="input-field" value={filters.inStock} onChange={handleFilterChange}>
            <option value="">Todos</option>
            <option value="true">Sí</option>
          </select>
          <div className="flex items-end gap-2">
            <button className="btn btn-primary" onClick={applyFilters}>Aplicar</button>
            <button className="btn btn-secondary" onClick={clearFilters}>Limpiar</button>
          </div>
        </div>
      </div>

      <div className="card table-container">
        <table className="w-full">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">ID</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Nombre</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Descripción</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Precio</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Stock</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Categoría</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Acciones</th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {products.map(p => (
              <tr key={p.id} className="hover:bg-gray-50">
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{p.id}</td>
                <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{p.name}</td>
                <td className="px-6 py-4 text-sm text-gray-500 max-w-xs truncate">{p.description}</td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">${p.price.toFixed(2)}</td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${p.stock > 0 ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}`}>{p.stock}</span>
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{p.category?.name}</td>
                <td className="px-6 py-4 whitespace-nowrap text-sm font-medium space-x-2">
                  <button className="btn btn-primary text-xs" onClick={() => navigate(`/edit-product/${p.id}`)}>Editar</button>
                  <button className="btn btn-danger text-xs" onClick={() => handleDelete(p.id)}>Eliminar</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {products.length === 0 && <div className="text-center py-8 text-gray-500">No se encontraron productos</div>}
      </div>

      <button className="btn btn-success mt-6" onClick={() => navigate('/add-product')}>+ Agregar Nuevo Producto</button>
    </div>
  );
};

export default ProductList;
